import Header from '../Header';
import WeHelpSurface from '../contentBlocks/page1WeHelpSurface';
import Page1Categories from '../contentBlocks/page1Categories';
import TeamMember from '../contentBlocks/page1MeetOur';
import SuccessStory from '../contentBlocks/page1SuccessStory';
// import RecentProjects from '../contentBlocks/page1RecentProjects';
import AkselNews from '../contentBlocks/AkselNews';
import DontMiss from '../contentBlocks/DontMiss';
import Footer from '../Footer/index';

export const HomePage = () => { // page 1


    const team = [
        {img: '/image/robot-human-silhouettes.jpg', 
            name: 'Vuong Cong Duy',
            job: 'Hanoi City, VietNam',
                  
        },
        {
            name: 'Nguyen Trung Kien',
            job: 'Hanoi City, VietNam',
            img: '/image/robot-human-silhouettes.jpg',
            },
        {
            name: 'Mai Ninh Binh',
            job: 'Hanoi Cỉy, VietNam',
            img: '/image/robot-human-silhouettes.jpg',
        },
        {
            name: 'Do Thai Binh',
            job: 'Hanoi Cỉy, VietNam',
            img: '/image/robot-human-silhouettes.jpg',
        },
    ];

    const news = [
        {
            title: 'Chung tay hỗ trợ sinh hoạt phí cho ba thiếu niên đặc biệt khó khăn tại Lào Cai',
            description: 'Dù mang cái tên rất đẹp – “Núi mặt trời mọc”, xã Thải Giàng Phố (huyện Bắc Hà, tỉnh Lào Cai) vẫn đang trong “cuộc chiến” xóa đói giảm nghèo khi tỉ lệ hộ nghèo và cận nghèo của toàn xã lên tới 62,37% (Theo Cổng thông tin điện tử tỉnh Lào Cai, 2023). Với những hạn chế về vị trí, điều kiện kinh tế và hỗ trợ xã hội, xã Thải Giàng Phố vẫn còn nhiều hoàn cảnh kém may mắn đang rất cần sự quan tâm và hỗ trợ từ cộng đồng.',
            author: 'Nguyen Thao Anh',
            image: '/image/huyen-pham-ZKcoColeq7U-unsplash.jpg' ,
            date: '4/3/2024',
        },
        {
            title: 'Gây Quỹ cho Vườn Xả',
            description: 'ườn Xả là một không gian sinh hoạt chung, nằm tại phường Ea Tam, thành phố Buôn Ma Thuột. Là một dự án phi lợi nhuận, Vườn Xả tạo nên một nơi chốn cho người lưu trú kết nối với chính mình, với người khác và với thiên nhiên. Thực hành lắng nghe không phán xét, tinh thần thấu cảm và lòng trắc ẩn, người đến Vườn cùng tạo ra một không gian để an trú và chữa lành qua việc làm vườn, đóng đồ gỗ, nấu ăn, yoga, thiền, trò chuyện, thảo luận sách và sáng tạo nghệ thuật. Tại đây, mọi người tự xây dựng sự vững chãi, yêu thương bản thân, khả năng tha thứ cho người khác và cho bản thân. Không gian Vườn Xả giúp mọi người được an ủi, tiếp sức lực tinh thần và làm mới cho cuộc sống. Từ khi mở cửa vào tháng 4/2022 tới cuối năm 2022, Vườn Xả đã tổ chức 9 khóa học, đem lại niềm tin, hy vọng và sự bình an cho gần 200 người lưu trú. ',
            author: 'Trinh Ngoc Lan',
            date: '20/4/2024',
            image: '/image/elliot-andrews-6keOhd7idJo-unsplash.jpg' 

        },
        {
            title: 'He Created the Web. Now He’s Out to Remake the Digital World.',
            description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.',
            author: 'Melinda Humbles',
            date: 'November 21th, 2020',
        },
    ];

    return (
        <section>
            <Header />
            <WeHelpSurface />
            <Page1Categories />
            <TeamMember team={team} />
            <SuccessStory />
            <AkselNews news={news} />
            <DontMiss />
            <Footer />
        </section>
    );
}
